import React from 'react'
import FavoritesLink from './FavoritesLink.js'
import './Sidebar.css'

export default class CategoriesDiv extends React.Component {
    categoriesArray = this.props.articles.categories.filter(function(item, pos, self) {
        return self.indexOf(item) == pos;
    })

    
   
    render() {
        return (
            <div id="categoriesDiv">
                {this.categoriesArray.map(function(category){
            return <FavoritesLink category={category}/>
            })}
            </div>
        );
    }
}
