import React from 'react'
//import './CommentView.css'

export default class CommentView extends React.Component {
    render() {
const users = this.props.users
   const user= users.filter(user => (user.id === this.props.comment.user_id))
   const userId = this.props.comment.user_id  
   const currentUserId = this.props.currentUser.id
   console.log(user[0])
    
    if (userId === currentUserId){
        return (
            <div id="commentContainer">
            <div id="articleComment">
                <div class="commentContent">
                    <div class="commenterName"><h1 class="commentOwner">{user[0].name}</h1></div>
                    <div class="commentText"><p>{this.props.comment.text}</p></div>
                </div>
                <div class="deleteComment">
                <button class="deleteCommentButton" onClick={() => { this.props.deleteComment(this.props.comment.id)} }>X</button>
                </div>
                
                </div>
                </div>

                
        );
    }

    return (
        <div id="commentContainer">
        <div id="articleComment">
            <div class="commentContent">
                <div class="commenterName"><h1 class="commentOwner">{user[0].name}</h1></div>
                <div class="commentText"><p>{this.props.comment.text}</p></div>
            </div>
            <div class="deleteComment">
            
            </div>
            
            </div>
            </div>
    );
}
    
}
