import React from 'react'
import { BrowserRouter, Route } from "react-router-dom";
import Sidebar from './Sidebar.js'
import ArticleList from './ArticleList.js'
import ArticleView from './ArticleView.js'
import './Dashboard.css'

export default class Dashboard extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            articles: [
                { id: 1, headline: "a string", summary: "Article 1 summary is a summary iof article 1. This will hold a small snippet of the article's text", text: "", tags: ["Front-End Development", "Responsive Design", "UX"], categories: ["Tutorial"], image: "IMAGE 1 URL" },
                { id: 2, headline: "another string", summary: "Article 2 summary is a summary iof article 2. This will hold a small snippet of the article's text", text: "", tags: ["Back-End Development", "Node", "Express"], categories: ["News", "New Technology"], image: "IMAGE 2 URL" },
                { id: 3, headline: "a string", summary: "Article 3 summary is a summary iof article 3. This will hold a small snippet of the article's text", text: "", tags: ["React", "Node", "Express"], categories: ["Tutorial"], image: "IMAGE 3 URL" },
            ],

        };
    }

    render() {
        return (<section id="dashboardContainer">
            <section id="Header">Header</section>
            <section id="navbar"></section>
            <Sidebar articles={this.state.articles} />
            <BrowserRouter>
                <Route exact path={'/dashboard'} component={() => <ArticleList articles={this.state.articles} />} />
                <Route exact path={'/dashboard/article'} component={ArticleView} />
                <Route exact path={'/dashboard/article/:id'} render={(props) => {
                    console.log(this.state.articles.find(a => a.id == props.match.params.id));
                    return <ArticleView article={this.state.articles.find(a => a.id == props.match.params.id)} />
                }} />
            </BrowserRouter>
        </section>
        );
    }
}
